mapper.select:
SELECT articleNO, title, content, imageFileName, id, parentNO,
writeDate, cat, sub
FROM t_board
WHERE 1=1
 AND parentNO = #{parentNO} 
 AND cat = #{cat} 
 AND sub = #{sub} 
ORDER BY articleNO DESC

	LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE t_board
SET title = #{title},
content = #{content},
cat = #{cat},
sub = #{sub}

	, imageFileName = #{imageFileName}

WHERE articleNO = #{articleNO}
